/*Table structure for table `clubact` */

DROP TABLE IF EXISTS `clubact`;

CREATE TABLE `clubact` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `type2` varchar(1) NOT NULL,
  `title` varchar(100) NOT NULL,
  `pubday` date NOT NULL,
  `content` mediumtext NOT NULL,
  `ispub` varchar(1) NOT NULL DEFAULT '1',
  `optime` datetime DEFAULT NULL,
  `puber` varchar(16) NOT NULL DEFAULT 'admin',
  PRIMARY KEY (`id`),
  KEY `type2` (`type2`),
  KEY `fk_puber` (`puber`),
  CONSTRAINT `fk_puber` FOREIGN KEY (`puber`) REFERENCES `siteuser` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

